using System;using System.Data.SqlClient;public partial class login : System.Web.UI.Page{    protected void btnLogin_Click(object sender, EventArgs e)    {        using (SqlConnection conn = Db.GetConnection())        {            string sql = "SELECT COUNT(*) FROM Users WHERE Username=@u AND Password=@p";            SqlCommand cmd = new SqlCommand(sql, conn);            cmd.Parameters.AddWithValue("@u", txtUser.Text);            cmd.Parameters.AddWithValue("@p", txtPass.Text);            int count = (int)cmd.ExecuteScalar();            if (count > 0)                Response.Redirect("dashboard.aspx");            else                lblMsg.Text = "Invalid login.";        }    }}